var sprefix = "z";
var n = msg.payload.nodes.length;
//n = 3;
global.set("server_id", "whatever");
var idlookup = { };
for (var i = 0; i < msg.payload.nodes.length; i++) {
    var station = msg.payload.nodes[i];
    if (station.type == "template")
        idlookup[station.id] = station.name;
}
for (var i = 0; i < n; i++) {
    var station = msg.payload.nodes[i];
    if (station.type != "template")
        continue;
    var nextstations = [ ];
    var nextstationnames = [ ];
    for (var j = 0; j < station.wires[0].length; j++) {
        var nwid = station.wires[0][j];
        if (idlookup[nwid]) {
            nextstations.push(sprefix + nwid);
            nextstationnames.push(idlookup[nwid]);
        }
    }
    node.send({ "name":station.name,
                "station_id":sprefix + station.id, 
                "next_stations":nextstations.join(" "), 
                "next_stationnames":nextstationnames.join(","),
                "payload":station.template,
                "entity_id":"text.nodered_"+sprefix+station.id});
}
return null;

for (var o of msg.payload) {
    if ((o["type"] == "tab") && (o["label"] == flow.get("tab_name")))
        return { "payload":o["id"] }; 
}
return null;

var c = flow.get("msgcount")|0;
msg.payload = msg.payload + String(c);
flow.set("msgcount", c + 1);
return msg;
